import React, { ChangeEvent } from 'react';
import {
  Box,
  TextField,
  Button,
  Typography,
  CircularProgress
} from '@mui/material';
import { Analysis } from '../types';

interface AnalysisFormProps {
  argument: string;
  analysis: Analysis | null;
  setAnalysis: (analysis: Analysis) => void;
  loading: boolean;
  onSubmit: () => void;
}

const AnalysisForm: React.FC<AnalysisFormProps> = ({
  argument,
  analysis,
  setAnalysis,
  loading,
  onSubmit
}) => {
  return (
    <Box>
      <Typography variant="h6" gutterBottom>
        논증문 분석
      </Typography>
      <Typography variant="body1" paragraph>
        {argument}
      </Typography>
      
      <TextField
        fullWidth
        multiline
        rows={3}
        label="주장 (쉼표로 구분)"
        value={analysis?.claims.join(', ') || ''}
        onChange={(e: ChangeEvent<HTMLTextAreaElement>) => setAnalysis({
          ...analysis || { evidence: [], structure: '', suggestions: [] },
          claims: e.target.value.split(',').map((claim: string) => claim.trim())
        })}
        sx={{ mb: 2 }}
      />
      
      <TextField
        fullWidth
        multiline
        rows={3}
        label="근거 (쉼표로 구분)"
        value={analysis?.evidence.join(', ') || ''}
        onChange={(e: ChangeEvent<HTMLTextAreaElement>) => setAnalysis({
          ...analysis || { claims: [], structure: '', suggestions: [] },
          evidence: e.target.value.split(',').map((evidence: string) => evidence.trim())
        })}
        sx={{ mb: 2 }}
      />
      
      <TextField
        fullWidth
        multiline
        rows={3}
        label="논증 구조"
        value={analysis?.structure || ''}
        onChange={(e: ChangeEvent<HTMLTextAreaElement>) => setAnalysis({
          ...analysis || { claims: [], evidence: [], suggestions: [] },
          structure: e.target.value
        })}
        sx={{ mb: 2 }}
      />

      <Button
        variant="contained"
        onClick={onSubmit}
        disabled={loading}
        sx={{ mt: 2 }}
      >
        {loading ? <CircularProgress size={24} /> : '분석 제출'}
      </Button>
    </Box>
  );
};

export default AnalysisForm;
