import React, { useState, ChangeEvent } from 'react';
import {
  Container,
  Typography,
  Card,
  CardContent,
  TextField,
  Button,
  Box,
  Alert,
  CircularProgress,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  SelectChangeEvent,
  Grid
} from '@mui/material';
import { OpenAI } from 'openai';
import AnalysisForm from './components/AnalysisForm';
import { Analysis, Difficulty, ContentType, VisualType, Evaluation } from './types';

declare global {
  interface Window {
    process: {
      env: {
        REACT_APP_OPENAI_API_KEY: string;
      };
    };
  }
}

const openai = new OpenAI({
  apiKey: process.env.REACT_APP_OPENAI_API_KEY,
  dangerouslyAllowBrowser: true
});

const difficultyPrompt: Record<Difficulty, string> = {
  easy: '한 단락으로 구성된 간단한 주장을 작성해주세요.',
  medium: '2-3개의 단락으로 구성된 논리적인 주장을 작성해주세요.',
  hard: '3-5개의 단락으로 구성된 복잡하고 심도 있는 주장을 작성해주세요.'
};

const App: React.FC = () => {
  const [topic, setTopic] = useState<string>('');
  const [difficulty, setDifficulty] = useState<Difficulty>('medium');
  const [contentType, setContentType] = useState<ContentType>('text');
  const [visualType, setVisualType] = useState<VisualType>('illustration');
  const [argument, setArgument] = useState<string>('');
  const [loading, setLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const [step, setStep] = useState<number>(0);
  const [analysis, setAnalysis] = useState<Analysis | null>(null);
  const [evaluation, setEvaluation] = useState<Evaluation | null>(null);
  const [generatedImage, setGeneratedImage] = useState<string>('');

  const handleDifficultyChange = (event: SelectChangeEvent<Difficulty>) => {
    setDifficulty(event.target.value as Difficulty);
  };

  const handleContentTypeChange = (event: SelectChangeEvent<ContentType>) => {
    setContentType(event.target.value as ContentType);
  };

  const handleVisualTypeChange = (event: SelectChangeEvent<VisualType>) => {
    setVisualType(event.target.value as VisualType);
  };

  const generateContent = async () => {
    setLoading(true);
    setError('');
    try {
      if (contentType === 'text') {
        await generateArgument();
      } else {
        await generateVisualContent();
      }
      setStep(1);
    } catch (err) {
      console.error('Content generation error:', err);
      setError('콘텐츠 생성 중 오류가 발생했습니다. 다시 시도해 주세요.');
    } finally {
      setLoading(false);
    }
  };

  const generateArgument = async () => {
    const completion = await openai.chat.completions.create({
      model: 'gpt-4-turbo-preview',
      messages: [
        {
          role: 'system',
          content: '당신은 주어진 주제에 대해 논리적이고 설득력 있는 주장을 작성하는 전문가입니다.',
        },
        {
          role: 'user',
          content: `주제: ${topic}\n난이도: ${difficulty}\n${difficultyPrompt[difficulty]}\n\n주장을 한국어로 작성해주세요.`,
        },
      ],
    });

    setArgument(completion.choices[0].message.content || '');
  };

  const generateVisualContent = async () => {
    try {
      let prompt = '';
      if (contentType === 'image') {
        prompt = `다음 주제에 대한 시각적 이미지를 생성해주세요: ${topic}. 이미지에는 한글 텍스트가 포함되어야 하며, 텍스트는 명확하고 읽기 쉬워야 합니다.`;
      } else if (contentType === 'graph') {
        prompt = `다음 주제에 대한 통계 그래프를 생성해주세요: ${topic}. 
        그래프는 다음 요구사항을 충족해야 합니다:
        1. 데이터를 명확하게 표현하는 적절한 그래프 유형 선택
        2. 한글로 된 제목, 축 레이블, 범례 포함
        3. 데이터 포인트에 대한 명확한 레이블링
        4. 시각적으로 구분이 쉬운 색상 사용
        5. 필요한 경우 추세선이나 주석 포함`;
      }

      const image = await openai.images.generate({
        model: 'dall-e-3',
        prompt: prompt,
        n: 1,
        size: '1024x1024',
        quality: 'hd',
        style: 'natural',
      });

      setGeneratedImage(image.data[0].url);
      setArgument(image.data[0].url); // 이미지 URL을 argument 상태에 저장
    } catch (err) {
      console.error('Visual content generation error:', err);
      throw new Error('시각적 콘텐츠 생성 중 오류가 발생했습니다.');
    }
  };

  const evaluateAnalysis = async (currentAnalysis: Analysis) => {
    setLoading(true);
    setError('');
    try {
      const response = await fetch('https://api.openai.com/v1/chat/completions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${process.env.REACT_APP_OPENAI_API_KEY}`
        },
        body: JSON.stringify({
          model: "gpt-4-turbo",
          messages: [
            {
              role: "system",
              content: `당신은 논증문 분석 전문가입니다. 다음 기준에 따라 사용자의 분석을 평가해주세요:

1. 주장 평가:
   - 핵심 주장의 정확성
   - 주요 키워드의 식별
   - 누락된 키워드 확인

2. 근거 평가:
   - 각 근거의 정확성
   - 키워드 식별의 적절성
   - 근거의 계층 구조 평가
   - 누락된 근거나 키워드 확인

3. 전체 구조 평가:
   - 근거 간 계층적 관계의 적절성
   - 전체 논증 구조의 완성도

평가 결과는 다음 JSON 형식으로 제공해주세요:
{
  "claimEvaluation": {
    "isAccurate": boolean,
    "feedback": string,
    "identifiedKeywords": boolean,
    "missedKeywords": string[]
  },
  "evidenceEvaluation": {
    "isAccurate": boolean,
    "feedback": string,
    "identifiedKeywords": boolean,
    "hasProperStructure": boolean,
    "missedKeywords": string[],
    "missedEvidence": string[]
  },
  "overallStructure": {
    "isHierarchical": boolean,
    "feedback": string
  }
}`
            },
            {
              role: "user",
              content: `논증문:\n${argument}\n\n사용자의 분석:\n${JSON.stringify(currentAnalysis, null, 2)}`
            }
          ]
        })
      });

      if (!response.ok) {
        throw new Error('평가 생성 중 오류가 발생했습니다.');
      }

      const data = await response.json();
      const evaluationResult = JSON.parse(data.choices[0].message.content);
      setEvaluation(evaluationResult);

      if (evaluationResult.claimEvaluation.needsRevision || 
          evaluationResult.evidenceEvaluation.hasProperStructure === false) {
        setStep(1.5);
      } else {
        setStep(2);
      }
    } catch (error) {
      console.error('분석 평가 오류:', error);
      setError(`오류가 발생했습니다: ${error instanceof Error ? error.message : '알 수 없는 오류'}`);
    } finally {
      setLoading(false);
    }
  };

  const submitAnalysis = async () => {
    await evaluateAnalysis(analysis);
  };

  return (
    <Container maxWidth="md" sx={{ mt: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom align="center">
        논증문 분석 연습
      </Typography>
      
      {error && (
        <Alert severity="error" sx={{ mb: 2 }}>
          {error}
        </Alert>
      )}

      {step === 0 && (
        <Card sx={{ mt: 4 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              분석할 화제를 입력하세요
            </Typography>
            <TextField
              fullWidth
              label="화제"
              value={topic}
              onChange={(e: ChangeEvent<HTMLInputElement>) => setTopic(e.target.value)}
              sx={{ mb: 2 }}
            />
            <Grid container spacing={2}>
              <Grid item xs={6}>
                <FormControl fullWidth sx={{ mb: 2 }}>
                  <InputLabel>난이도</InputLabel>
                  <Select
                    value={difficulty}
                    label="난이도"
                    onChange={handleDifficultyChange}
                  >
                    <MenuItem value="easy">쉬움</MenuItem>
                    <MenuItem value="medium">보통</MenuItem>
                    <MenuItem value="hard">어려움</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
              <Grid item xs={6}>
                <FormControl fullWidth sx={{ mb: 2 }}>
                  <InputLabel>콘텐츠 유형</InputLabel>
                  <Select
                    value={contentType}
                    label="콘텐츠 유형"
                    onChange={handleContentTypeChange}
                  >
                    <MenuItem value="text">텍스트</MenuItem>
                    <MenuItem value="image">이미지</MenuItem>
                  </Select>
                </FormControl>
              </Grid>
            </Grid>

            {contentType === 'image' && (
              <FormControl fullWidth sx={{ mb: 2 }}>
                <InputLabel>이미지 유형</InputLabel>
                <Select
                  value={visualType}
                  label="이미지 유형"
                  onChange={handleVisualTypeChange}
                >
                  <MenuItem value="illustration">일러스트레이션</MenuItem>
                  <MenuItem value="infographic">인포그래픽</MenuItem>
                  <MenuItem value="statistics">통계 그래프</MenuItem>
                </Select>
              </FormControl>
            )}

            <Button
              variant="contained"
              onClick={generateContent}
              disabled={!topic || loading}
            >
              {loading ? <CircularProgress size={24} /> : '생성하기'}
            </Button>
          </CardContent>
        </Card>
      )}

      {step >= 1 && contentType === 'text' && (
        <Card sx={{ mt: 4 }}>
          <CardContent>
            <AnalysisForm
              argument={argument}
              analysis={analysis}
              setAnalysis={setAnalysis}
              loading={loading}
              onSubmit={submitAnalysis}
            />
          </CardContent>
        </Card>
      )}

      {evaluation && step >= 1.5 && (
        <Card sx={{ mt: 4 }}>
          <CardContent>
            <Typography variant="h6" gutterBottom>
              평가 결과
            </Typography>
            
            <Box sx={{ mb: 3 }}>
              <Typography variant="subtitle1" gutterBottom>
                주장 평가
              </Typography>
              <Typography>
                {evaluation.claimEvaluation.feedback}
              </Typography>
              {evaluation.claimEvaluation.missedKeywords && 
               evaluation.claimEvaluation.missedKeywords.length > 0 && (
                <Typography color="error">
                  누락된 키워드: {evaluation.claimEvaluation.missedKeywords.join(', ')}
                </Typography>
              )}
            </Box>

            <Box sx={{ mb: 3 }}>
              <Typography variant="subtitle1" gutterBottom>
                근거 평가
              </Typography>
              <Typography>
                {evaluation.evidenceEvaluation.feedback}
              </Typography>
              {evaluation.evidenceEvaluation.missedKeywords && 
               evaluation.evidenceEvaluation.missedKeywords.length > 0 && (
                <Typography color="error">
                  누락된 키워드: {evaluation.evidenceEvaluation.missedKeywords.join(', ')}
                </Typography>
              )}
              {evaluation.evidenceEvaluation.missedEvidence && 
               evaluation.evidenceEvaluation.missedEvidence.length > 0 && (
                <Typography color="error">
                  누락된 근거: {evaluation.evidenceEvaluation.missedEvidence.join(', ')}
                </Typography>
              )}
            </Box>

            <Box sx={{ mb: 3 }}>
              <Typography variant="subtitle1" gutterBottom>
                전체 구조 평가
              </Typography>
              <Typography>
                {evaluation.overallStructure.feedback}
              </Typography>
            </Box>

            <Button
              variant="contained"
              onClick={() => setStep(1)}
              sx={{ mr: 2 }}
            >
              수정하기
            </Button>
          </CardContent>
        </Card>
      )}
    </Container>
  );
};

export default App;
